import React from 'react';
import { useBasket } from '../context/BasketContext';

const BasketPage = () => {
    const { basket, removeFromBasket } = useBasket();  // Получаем корзину и функцию удаления товара

    return (
        <div>
            <h2>Корзина</h2>
            {basket.length === 0 ? (
                <p>Корзина пуста</p>
            ) : (
                <ul>
                    {basket.map((device) => (
                        <li key={device.id}>
                            <img
                                src={`http://localhost:5000/static/${device.img}`}
                                alt={device.name}
                                width="50"
                                onError={(e) => e.target.src = '/default-image.jpg'}
                            />

                            {device.name} - {device.price} ₽
                            <button onClick={() => removeFromBasket(device.id)}>Удалить</button>
                        </li>
                    ))}
                </ul>

            )}
        </div>
    );
};

export default BasketPage;
