import React from 'react';
import { useBasket } from '../context/BasketContext';
import '../styles/BasketPage.css'; // –ü–æ–¥–∫–ª—é—á–∞–µ–º CSS

const BasketPage = () => {
    const { basket, removeFromBasket } = useBasket();

    // –ü–æ–¥—Å—á–µ—Ç –æ–±—â–µ–π —Å—Ç–æ–∏–º–æ—Å—Ç–∏
    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

    return (
        <div className="basket-container">
            <h2 className="basket-title">üõí –ö–æ—Ä–∑–∏–Ω–∞</h2>

            {basket.length === 0 ? (
                <p className="empty-message">–ö–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞</p>
            ) : (
                <ul>
                    {basket.map((device) => (
                        <li key={device.basketId}>  {/* –ò—Å–ø–æ–ª—å–∑—É–µ–º basketId –∫–∞–∫ —É–Ω–∏–∫–∞–ª—å–Ω—ã–π –∫–ª—é—á */}
                            <img
                                src={`http://localhost:5000/static/${device.img}`}
                                width={70}
                                alt={device.name}
                            />
                            {device.name} - {device.price} ‚ÇΩ
                            <button onClick={() => removeFromBasket(device.basketId)}>–£–¥–∞–ª–∏—Ç—å</button>
                        </li>
                    ))}
                </ul>
            )}

            {/* –§—É—Ç–µ—Ä —Å –∏—Ç–æ–≥–æ–≤–æ–π —Ü–µ–Ω–æ–π –∏ –∫–Ω–æ–ø–∫–æ–π –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è */}
            {basket.length > 0 && (
                <footer className="basket-footer">
                    <div className="total-price">–ò—Ç–æ–≥–æ: <strong>{totalPrice} ‚ÇΩ</strong></div>
                    <button className="checkout-btn">–û—Ñ–æ—Ä–º–∏—Ç—å –∑–∞–∫–∞–∑</button>
                </footer>
            )}
        </div>
    );
};

export default BasketPage;
