import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrders = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        axios.get('/api/orders', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
            .then(response => {
                setOrders(response.data);
            })
            .catch(error => {
                console.error("Ошибка при получении заказов:", error);
            });
    }, []);

    return (
        <div>
            <h2>Все заказы</h2>
            {orders.length > 0 ? (
                <table>
                    <thead>
                    <tr>
                        <th>Номер заказа</th>
                        <th>Пользователь</th>
                        <th>Статус</th>
                        <th>Сумма</th>
                        <th>Товары</th>
                    </tr>
                    </thead>
                    <tbody>
                    {orders.map(order => (
                        <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.userId}</td>
                            <td>{order.status}</td>
                            <td>{order.totalPrice} ₽</td>
                            <td>
                                {order.order_devices.map(device => (
                                    <p key={device.deviceId}>{device.device.name} - Количество: {device.quantity}</p>
                                ))}
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            ) : (
                <p>Нет заказов.</p>
            )}
        </div>
    );
};

export default AdminOrders;
