import React from 'react';
import { useBasket } from '../context/BasketContext';  // Подключаем хук из контекста
import { Container, Row, Col, Button, Card } from 'react-bootstrap';

const Basket = () => {
    const { basket, removeFromBasket, clearBasket } = useBasket();  // Получаем данные корзины и функции из контекста

    // Если корзина пуста, выводим сообщение
    if (basket.length === 0) {
        return <Container><h2>Корзина пуста</h2></Container>;
    }

    return (
        <Container>
            <h2>Корзина</h2>
            <Row>
                {basket.map((device) => (
                    <Col md={4} key={device.id} className="mt-3">
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src={process.env.REACT_APP_API_URL + device.img} />
                            <Card.Body>
                                <Card.Title>{device.name}</Card.Title>
                                <Card.Text>{device.price} ₽</Card.Text>
                                <Button variant="danger" onClick={() => removeFromBasket(device.id)}>
                                    Удалить из корзины
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            <Row>
                <Col className="mt-3">
                    <Button variant="warning" onClick={clearBasket}>
                        Очистить корзину
                    </Button>
                </Col>
            </Row>
        </Container>
    );
};

export default Basket;
