import React from 'react';
import { useBasket } from '../context/BasketContext';  // Используем useBasket
import { Button } from 'react-bootstrap';

const BasketPage = () => {
    const { basket, removeFromBasket } = useBasket();  // Получаем корзину и функцию удаления

    return (
        <div>
            <h2>Корзина</h2>
            {basket.length === 0 ? (
                <p>Корзина пуста</p>
            ) : (
                basket.map(item => (
                    <div key={item.id}>
                        <h4>{item.name}</h4>
                        <p>{item.price} ₽</p>
                        <Button variant="danger" onClick={() => removeFromBasket(item.id)}>
                            Удалить
                        </Button>
                    </div>
                ))
            )}
        </div>
    );
};

export default BasketPage;
