import React, { useEffect, useState } from 'react';
import { Col, Container, Row, Card, Button, Image } from "react-bootstrap";
import star from '../assets/star.png';
import { useParams } from 'react-router-dom';
import { fetchOneDevice } from "../http/deviceAPI";
import { useBasket } from '../context/BasketContext';  // Используем правильный импорт

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const { addToBasket } = useBasket();  // Делаем доступной функцию добавления в корзину

    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data));
    }, [id]);

    const handleAddToBasket = () => {
        addToBasket(device);  // Добавляем товар в корзину
    };

    return (
        <Container>
            <Row>
                <Col md={4} className="mt-3">
                    <Image width={300} height={300} src={process.env.REACT_APP_API_URL + device.img} />
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                background: `url(${star}) no-repeat center center`,
                                width: 240,
                                height: 240,
                                backgroundSize: "cover",
                                fontSize: 64
                            }}
                        >
                            {device.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{ width: 300, height: 300, fontSize: 32, border: '5px solid lightgray' }}
                    >
                        <h3>{device.price} ₽</h3>
                        <Button variant={"outline-dark"} onClick={handleAddToBasket}>
                            Добавить в корзину
                        </Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Settings</h1>
                {device.info.map((info, index) =>
                    <Row key={info.id} style={{ background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10 }}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DevicePage;
