import React from "react";
import { Container, Button, Card } from "react-bootstrap";
import { useBasket } from "../context/BasketContext";
import { useNavigate } from "react-router-dom";
import { SHOP_ROUTE } from "../utils/consts";

const PaymentPage = () => {
    const { basket } = useBasket();
    const navigate = useNavigate();


    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

    const handlePayment = () => {
        alert("–û–ø–ª–∞—Ç–∞ –ø—Ä–æ—à–ª–∞ —É—Å–ø–µ—à–Ω–æ! üéâ");
        navigate(SHOP_ROUTE);
    };

    return (
        <Container className="d-flex justify-content-center align-items-center" style={{ height: "100vh" }}>
            <Card style={{ width: 400 }} className="p-4 text-center shadow-lg">
                <h3>–û–ø–ª–∞—Ç–∞ –∑–∞–∫–∞–∑–∞</h3>
                <p>–ò—Ç–æ–≥–æ–≤–∞—è —Å—É–º–º–∞: <strong>{totalPrice} ‚ÇΩ</strong></p>
                <Button variant="success" size="lg" onClick={handlePayment}>
                    –û–ø–ª–∞—Ç–∏—Ç—å
                </Button>
                <Button variant="secondary" size="lg" className="mt-3" onClick={() => navigate(SHOP_ROUTE)}>
                    –í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –º–∞–≥–∞–∑–∏–Ω
                </Button>
            </Card>
        </Container>
    );
};

export default PaymentPage;
