import React, {useContext} from 'react';
import Card from "react-bootstrap/Card";
import {observer} from "mobx-react-lite";
import Row from "react-bootstrap/Row";
import {Context} from "../index";


const BrandBar = observer(() => {
    const {device} = useContext(Context)
    return (
        <Row className="d-flex">
            {device.brands.map(brand =>
                <Card
                    key={brand.id}
                    className="p-3 m-2 d-flex align-items-center justify-content-center"
                    style={{
                        flex: '1 1 20%',   // Flex-grow, flex-shrink и flex-basis
                        minWidth: '20px', // Минимальная ширина карточки
                        maxWidth: '100px', // Максимальная ширина карточки
                        height: '100px',   // Высота карточки
                        textAlign: 'center',
                        border: '1px solid #ddd',  // Легкая граница для карточек
                    }}
                >
                    {brand.name}
                </Card>
            )}
        </Row>
    );
});
export default BrandBar;
