import React, {useContext} from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import {Context} from '../index'
import {NavLink} from "react-bootstrap";
import {ADMIN_ROUTE, LOGIN_ROUTE, SHOP_ROUTE} from "../utils/consts";
import {observer} from "mobx-react-lite";
import {useNavigate} from "react-router-dom";
import Button from "react-bootstrap/Button";

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const navigate = useNavigate(); // Используем navigate для редиректа
    return (
            <Navbar bg="dark" data-bs-theme="dark">
                <Container>
                    <NavLink style={{color: "white"}} to={SHOP_ROUTE}>Buy Device</NavLink>
                    {user.isAuth ?
                        <Nav className="ml-auto">
                            <Button
                                className="ml-2"
                                onClick={() => navigate(ADMIN_ROUTE)} // Здесь используем navigate вместо href
                            >Admin</Button>
                            <Button
                                className="ml-2"
                                onClick={() => {// Логика для выхода
                                    navigate(LOGIN_ROUTE); // Переходим на страницу логина
                                }}>
                                Login
                            </Button>
                        </Nav>
                        :
                        <Nav className="ma-auto">
                            <Nav.Link
                                href="#home"
                                onClick={() => user.setIsAuth(true)}
                            >Login</Nav.Link>
                        </Nav>
                    }
                </Container>
            </Navbar>
        );
    }
)

export default NavBar;
