import "../styles/DevicePage.css";
import React, { useEffect, useState } from "react";
import { Button, Card, Col, Container, Image, Row } from "react-bootstrap";
import bigStar from "../assets/star.png";
import { useParams } from "react-router-dom";
import { fetchOneDevice } from "../http/deviceAPI";
import axios from "axios";
import { jwtDecode as jwt_decode } from "jwt-decode";


const StyledRating = styled(Rating)(({ theme }) => ({
    '& .MuiRating-iconEmpty .MuiSvgIcon-root': {
        color: theme.palette.action.disabled,
    },
}));

const customIcons = {
    1: {
        icon: <SentimentVeryDissatisfiedIcon color="error" />,
        label: 'Very Dissatisfied',
    },
    2: {
        icon: <SentimentDissatisfiedIcon color="error" />,
        label: 'Dissatisfied',
    },
    3: {
        icon: <SentimentSatisfiedIcon color="warning" />,
        label: 'Neutral',
    },
    4: {
        icon: <SentimentSatisfiedAltIcon color="success" />,
        label: 'Satisfied',
    },
    5: {
        icon: <SentimentVerySatisfiedIcon color="success" />,
        label: 'Very Satisfied',
    },
};

function IconContainer(props: IconContainerProps) {
    const { value, ...other } = props;
    return <span {...other}>{customIcons[value].icon}</span>;
}

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const [showImageModal, setShowImageModal] = useState(false);
    const [newRating, setNewRating] = useState(device.rating);

    useEffect(() => {
        fetchOneDevice(id).then((data) => setDevice(data));
    }, [id]);

    const handleRating = async (rating) => {
        try {
            const token = localStorage.getItem("token");

            if (!token) {
                console.error("User not logged in");
                return;
            }

            const decodedToken = jwt_decode(token);
            const userId = decodedToken.id;

            const response = await axios.post("http://localhost:5000/api/rate", {
                userId: userId,
                deviceId: id,
                newRating: rating,
            });

            setDevice((prevState) => ({
                ...prevState,
                rating: response.data.newRating,
            }));

            setNewRating(rating);
        } catch (error) {
            console.error("Error updating rating:", error);
        }
    };

    return (
        <Container className="device-page-container mt-3">
            <Row>
                <Col md={4}>
                    <Image
                        className="device-image"
                        width={300}
                        height={300}
                        src={process.env.REACT_APP_API_URL + device.img}
                        alt={device.name}
                        onClick={() => setShowImageModal(true)}
                    />
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2 className="device-title">{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                background: `url(${bigStar}) no-repeat center center`,
                                width: 240,
                                height: 240,
                                backgroundSize: "cover",
                                fontSize: 64,
                            }}
                        >
                            {device.rating}
                        </div>
                        <div className="rating-buttons">
                            <StyledRating
                                name="highlight-selected-only"
                                value={newRating}
                                IconContainerComponent={IconContainer}
                                onChange={(event, newValue) => handleRating(newValue)}
                                highlightSelectedOnly
                            />
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card className="device-price-card d-flex flex-column align-items-center justify-content-around">
                        <h3>От: {device.price} руб.</h3>
                        <Button variant={"outline-dark"}>Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="device-info">
                <h1>Характеристики</h1>
                {device.info.map((info, index) => (
                    <Row
                        key={info.id}
                        className="info-row"
                        style={{ background: index % 2 === 0 ? "lightgray" : "transparent" }}
                    >
                        {info.title}: {info.description}
                    </Row>
                ))}
            </Row>

            {showImageModal && (
                <div className="image-modal" onClick={() => setShowImageModal(false)}>
                    <div className="modal-content">
                        <Image
                            src={process.env.REACT_APP_API_URL + device.img}
                            alt={device.name}
                            className="modal-image"
                        />
                    </div>
                </div>
            )}
        </Container>
    );
};

export default DevicePage;
