import React, { createContext, useState, useContext } from 'react';

const BasketContext = createContext();

export const useBasket = () => {
    return useContext(BasketContext);  // Возвращаем доступ к корзине
};

export const BasketProvider = ({ children }) => {
    const [basket, setBasket] = useState([]);

    const handleAddToBasket = () => {
        console.log("Adding to basket", device);  // Логируем устройство, которое добавляется
        addToBasket(device);  // Добавляем товар в корзину
    };


    const removeFromBasket = (id) => {
        setBasket(basket.filter(item => item.id !== id));  // Удаляем товар из корзины
    };

    return (
        <BasketContext.Provider value={{ basket, addToBasket, removeFromBasket }}>
            {children}
        </BasketContext.Provider>
    );
};
