import React from 'react';
import { useBasket } from '../context/BasketContext';
import '../styles/BasketPage.css';
import { useNavigate } from 'react-router-dom';  // Импортируем хук useNavigate
import { PAYMENT_ROUTE } from "../utils/consts";

const BasketPage = () => {
    const { basket, removeFromBasket } = useBasket();
    const navigate = useNavigate();  // Инициализируем useNavigate

    // Вычисляем общую стоимость
    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

    // Функция для оформления заказа
    const handleCheckout = () => {
        navigate(PAYMENT_ROUTE);  // Переходим на страницу оплаты
    };

    return (
        <div className="basket-page">
            <h2>Корзина</h2>
            {basket.length === 0 ? (
                <p>Корзина пуста</p>
            ) : (
                <ul className="basket-items">
                    {basket.map((item) => (
                        <li key={item.basketId} className="basket-item">
                            <img
                                src={item.img}
                                alt={item.name}
                                width={70}
                                className="basket-item-img"
                            />
                            <span>{item.name}</span> - {item.price} ₽
                            <button onClick={() => removeFromBasket(item.basketId)} className="remove-btn">
                                Удалить
                            </button>
                        </li>
                    ))}
                </ul>
            )}
            {/* Footer с общей стоимостью и кнопкой оформления заказа */}
            <footer className="basket-footer">
                <div className="total-price">
                    Итого: <strong>{totalPrice} ₽</strong>
                </div>
                <button className="checkout-btn" onClick={handleCheckout}>
                    Оформить заказ
                </button>
            </footer>
        </div>
    );
};

export default BasketPage;
