import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const Basket = () => {
    // Пример данных о товарах в корзине
    const initialItems = [
        { id: 1, name: 'Телефон', price: 20000, quantity: 1 },
        { id: 2, name: 'Ноутбук', price: 50000, quantity: 1 }
    ];

    const [items, setItems] = useState(initialItems);

    const removeItem = (id) => {
        setItems(items.filter(item => item.id !== id));  // Удаляем товар из корзины
    };

    const changeQuantity = (id, quantity) => {
        setItems(items.map(item =>
            item.id === id ? { ...item, quantity: quantity } : item
        ));
    };

    const calculateTotal = () => {
        return items.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    return (
        <div>
            <h2>Корзина</h2>
            <div>
                {items.length === 0 ? (
                    <p>Ваша корзина пуста</p>
                ) : (
                    <ul>
                        {items.map(item => (
                            <li key={item.id}>
                                <h5>{item.name}</h5>
                                <p>Цена: {item.price} руб.</p>
                                <p>
                                    Количество:
                                    <Button variant="outline-secondary" onClick={() => changeQuantity(item.id, item.quantity - 1)}>-</Button>
                                    {item.quantity}
                                    <Button variant="outline-secondary" onClick={() => changeQuantity(item.id, item.quantity + 1)}>+</Button>
                                </p>
                                <Button variant="danger" onClick={() => removeItem(item.id)}>Удалить</Button>
                                <hr />
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <h4>Общая сумма: {calculateTotal()} руб.</h4>
            <Button variant="primary">Оформить заказ</Button>
        </div>
    );
};

export default Basket;
