import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const $host = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
});

const $authHost = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
});

const authInterceptor = config => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.authorization = `Bearer ${token}`;
    }
    return config;
};

// Ошибка интерсептора для перехвата 401 ошибок
const errorInterceptor = (error) => {
    if (error.response && error.response.status === 401) {
        // Получаем текущий путь для предотвращения бесконечного редиректа
        const currentPath = window.location.pathname;

        // Проверяем, если пользователь уже на странице логина, то не делать редирект
        if (currentPath !== '/login') {
            // Используем navigate для корректного редиректа
            window.location.href = '/login'; // или navigate('/login') в компонентах с использованием useNavigate
        }
    }
    return Promise.reject(error);
};

$authHost.interceptors.request.use(authInterceptor);
$authHost.interceptors.response.use(response => response, errorInterceptor);

export { $host, $authHost };
