import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBasket } from '../context/BasketContext';  // Импортируем контекст корзины
import { SHOP_ROUTE } from '../utils/consts';  // Путь к странице магазина

const PaymentPage = () => {
    const { clearBasket } = useBasket();  // Получаем метод для очистки корзины
    const [paymentStatus, setPaymentStatus] = useState(null);  // Состояние для статуса платежа
    const navigate = useNavigate();

    const handlePayment = () => {


        const isPaymentSuccessful = true;

        if (isPaymentSuccessful) {
            setPaymentStatus('Платеж прошел успешно!');
            clearBasket();
            setTimeout(() => {
                navigate(SHOP_ROUTE);
            }, 2000);
        } else {
            setPaymentStatus('Ошибка при оплате');
        }
    };

    return (
        <div className="payment-page">
            <h2>Оформление заказа</h2>

            {paymentStatus && <p>{paymentStatus}</p>}

            <button onClick={handlePayment} className="payment-btn">
                Оплатить
            </button>
        </div>
    );
};

export default PaymentPage;
