import React, { useContext, useEffect, useState } from 'react';
import { BrowserRouter } from "react-router-dom";
import AppRouter from "./components/AppRouter";
import NavBar from './components/NavBar';
import { observer } from "mobx-react-lite";
import { Context } from "./index";
import { check } from "./http/userAPI";
import { Spinner } from "react-bootstrap";

const App = observer(() => {
    const { user } = useContext(Context);
    const [loading, setLoading] = useState(true);

    // При загрузке приложения проверяем наличие токена
    useEffect(() => {
        check()
            .then(data => {
                if (data) {
                    user.setUser(data);
                    user.setIsAuth(true);
                }
            })
            .catch(() => {
                user.setUser({});
                user.setIsAuth(false);
                localStorage.removeItem("token");
            })
            .finally(() => setLoading(false)); // Завершаем загрузку после проверки
    }, []);

    // Если состояние еще не загружено, показываем Spinner
    if (loading) {
        return <Spinner animation={'grow'} />;
    }

    return (
        <BrowserRouter>
            <NavBar />
            {/* Теперь передаем `loading` как пропс в AppRouter */}
            <AppRouter />
        </BrowserRouter>
    );
});

export default App;
