import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBasket } from '../context/BasketContext';
import { SHOP_ROUTE } from '../utils/consts';
import axios from 'axios';
import '../styles/PaymentPage.css';

const PaymentPage = () => {
    const { basket, clearBasket } = useBasket();
    const [paymentStatus, setPaymentStatus] = useState(null);
    const [paymentMessage, setPaymentMessage] = useState("");
    const [address, setAddress] = useState("");
    const navigate = useNavigate();

    const userId = localStorage.getItem('userId');
    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

    const handlePayment = async () => {
        if (!address) {
            setPaymentMessage('Пожалуйста, введите ваш адрес.');
            setPaymentStatus('error');
            return;
        }

        if (!userId) {
            setPaymentMessage('Ошибка: пользователь не авторизован.');
            setPaymentStatus('error');
            return;
        }

        if (basket.length === 0) {
            setPaymentMessage('Корзина пуста. Добавьте товары перед заказом.');
            setPaymentStatus('error');
            return;
        }

        try {
            const orderDevices = basket.map(item => ({
                deviceId: item.id,
                price: item.price || 0,
            }));

            const response = await axios.post('http://localhost:5000/api/orders/create', {
                userId,
                orderDevices,
                totalPrice,
                address,
            }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });

            console.log('Ответ сервера:', response.data);

            setPaymentMessage('Ваш заказ был успешно создан!');
            setPaymentStatus('success');
            clearBasket();

            setTimeout(() => {
                navigate(SHOP_ROUTE);
            }, 2000);

        } catch (error) {
            console.error("Ошибка при создании заказа:", error);
            setPaymentMessage(error.response?.data?.message || 'Ошибка при создании заказа');
            setPaymentStatus('error');
        }
    };

    return (
        <div className="payment-page">
            <h2>Оформление заказа</h2>

            <div className="payment-items">
                <h3>Товары в заказе:</h3>
                <ul>
                    {basket.map((item) => (
                        <li key={item.id} className="payment-item">
                            {item.name} - {item.price} ₽
                        </li>
                    ))}
                </ul>
            </div>

            <div className="total-price">
                <h3>Итого: <strong>{totalPrice} ₽</strong></h3>
            </div>

            <div className="address-input">
                <label htmlFor="address">Введите ваш адрес:</label>
                <input
                    type="text"
                    id="address"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Введите ваш адрес"
                />
            </div>

            <div className="payment-instruction">
                <p><strong>Важно:</strong> После оформления заказа выберите способ оплаты «Наличные при получении». Ваши товары будут доставлены по указанному адресу.</p>
            </div>

            {paymentStatus === 'success' && <p className="success-message">{paymentMessage}</p>}
            {paymentStatus === 'error' && <p className="error-message">{paymentMessage}</p>}

            <button onClick={handlePayment} className="payment-btn">
                Заказать
            </button>
        </div>
    );
};

export default PaymentPage;
