import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBasket } from '../context/BasketContext';  // Импортируем контекст корзины
import { SHOP_ROUTE } from '../utils/consts';  // Путь к странице магазина
import '../styles/PaymentPage.css';  // Подключаем стили

const PaymentPage = () => {
    const { basket, clearBasket } = useBasket();  // Получаем корзину и метод для очистки корзины
    const [paymentStatus, setPaymentStatus] = useState(null);  // Состояние для статуса платежа
    const [paymentMessage, setPaymentMessage] = useState("");  // Сообщение для отображения
    const navigate = useNavigate();


    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);


    const handlePayment = () => {

        const isPaymentSuccessful = true;  // Здесь будет ваша логика платежа

        if (isPaymentSuccessful) {
            setPaymentMessage('Заказ прошел успешно!');
            setPaymentStatus('success');
            clearBasket();  // Очищаем корзину после успешной оплаты
            setTimeout(() => {
                navigate(SHOP_ROUTE);  // Перенаправляем в магазин через 2 секунды
            }, 800);
        } else {
            setPaymentMessage('Ошибка при оплате');
            setPaymentStatus('error');
        }
    };

    return (
        <div className="payment-page">
            <h2>Оформление заказа</h2>

            <div className="payment-items">
                <h3>Товары в заказе:</h3>
                <ul>
                    {basket.map((item) => (
                        <li key={item.id} className="payment-item">
                            {item.name} - {item.price} ₽
                        </li>
                    ))}
                </ul>
            </div>

            <div className="total-price">
                <h3>Итого: <strong>{totalPrice} ₽</strong></h3>
            </div>

            <div className="payment-instruction">
                <p><strong>Важно:</strong> После оформления заказа и перехода к оплате, выберите способ оплаты «Наличные при получении». Ваши товары будут доставлены по указанному адресу. При получении заказа передайте курьеру нужную сумму. Пожалуйста, убедитесь, что все товары в заказе верны и в хорошем состоянии. Благодарим за покупку!</p>
            </div>

            {paymentStatus === 'success' && (
                <p className="success-message">{paymentMessage}</p>
            )}

            {paymentStatus === 'error' && (
                <p className="error-message">{paymentMessage}</p>
            )}

            <button onClick={handlePayment} className="payment-btn">
                Заказать
            </button>
        </div>
    );
};

export default PaymentPage;
