import React, { useState, useEffect } from 'react';

const AdminOrders = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await fetch('/api/orders');
            const text = await response.text(); // Получаем ответ как текст

            // Проверяем, является ли ответ JSON
            try {
                const data = JSON.parse(text);
                // Проверка на массив
                if (Array.isArray(data)) {
                    setOrders(data);
                } else {
                    console.error('Получены некорректные данные:', data);
                }
            } catch (error) {
                console.error('Ошибка при парсинге JSON:', error);
                console.error('Ответ сервера:', text);
            }
        } catch (error) {
            console.error('Ошибка при загрузке заказов:', error);
        }
    };


    return (
        <div>
            <h2>Все заказы</h2>
            {orders && orders.length > 0 ? (
                orders.map(order => (
                    <div key={order.id}>
                        <h3>Заказ {order.id}</h3>
                        <p>Цена: {order.totalPrice} руб.</p>
                        <p>Дата заказа: {new Date(order.createdAt).toLocaleDateString()}</p>
                        <ul>
                            {order.orderDevices && order.orderDevices.map(device => (
                                <li key={device.id}>
                                    {device.device.name} - {device.price} руб.
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>Нет заказов</p>
            )}
        </div>
    );
};

export default AdminOrders;
