import "../styles/DevicePage.css";
import React, { useEffect, useState } from "react";
import { Button, Card, Col, Container, Image, Row } from "react-bootstrap";
import bigStar from "../assets/star.png";
import { useParams } from "react-router-dom";
import { fetchOneDevice } from "../http/deviceAPI";
import axios from "axios";
import { jwtDecode as jwt_decode } from "jwt-decode";

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const [showImageModal, setShowImageModal] = useState(false);
    const [newRating, setNewRating] = useState(0);

    useEffect(() => {
        fetchOneDevice(id).then((data) => {
            setDevice(data);
            setNewRating(parseFloat(data.rating) || 0); // Преобразуем в число или 0, если рейтинг не задан
        });
    }, [id]);

    const handleRating = async (rating) => {
        try {
            const token = localStorage.getItem("token");
            if (!token) {
                console.error("User not logged in");
                return;
            }

            const decodedToken = jwt_decode(token);
            const userId = decodedToken.id;

            const response = await axios.post("http://localhost:5000/api/rate", {
                userId: userId,
                deviceId: id,
                newRating: parseFloat(rating),  // Преобразуем рейтинг в FLOAT перед отправкой
            });

            setDevice((prevState) => ({
                ...prevState,
                rating: parseFloat(response.data.newRating) || 0, // Преобразуем в число или 0, если рейтинг не задан
            }));

            setNewRating(rating); // Обновляем локальное состояние рейтинга
        } catch (error) {
            console.error("Error updating rating:", error);
        }
    };

    // Проверка, что rating существует и является числом
    const rating = device.rating !== undefined && device.rating !== null ? device.rating : 0;

    const handleAddToBasket = () => {
        // Получаем текущую корзину из localStorage или создаем новую
        const basket = JSON.parse(localStorage.getItem('basket')) || [];

        // Добавляем текущий товар в корзину
        const newItem = {
            id: device.id,
            name: device.name,
            price: device.price,
            img: device.img,
        };
        
    };

    return (
        <Container className="device-page-container mt-3">
            <Row>
                <Col md={4}>
                    <Image
                        className="device-image"
                        width={300}
                        height={300}
                        src={process.env.REACT_APP_API_URL + device.img}
                        alt={device.name}
                        onClick={() => setShowImageModal(true)}
                    />
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2 className="device-title">{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                        >
                            <div className="device-rating">
                                {/* Если рейтинг есть, отобразим с 1 знаков после запятой */}
                                {rating !== 0 ? rating.toFixed(1) : "Нет рейтинга"}
                                <img src={bigStar} alt="star" width={25} />
                            </div>
                        </div>
                        <div className="rating-buttons">
                            {[1, 2, 3, 4, 5].map((star) => (
                                <Button
                                    key={star}
                                    variant={star <= newRating ? "warning" : "outline-warning"}
                                    onClick={() => handleRating(star)}
                                >
                                    {star}
                                </Button>
                            ))}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card className="device-price-card d-flex flex-column align-items-center justify-content-around">
                        <h3>От: {device.price} руб.</h3>
                        <Button
                            variant={"outline-dark"}
                            onClick={handleAddToBasket}
                        >
                            Добавить в корзину
                        </Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {device.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>

            {showImageModal && (
                <div className="image-modal" onClick={() => setShowImageModal(false)}>
                    <div className="modal-content">
                        <Image
                            src={process.env.REACT_APP_API_URL + device.img}
                            alt={device.name}
                            className="modal-image"
                        />
                    </div>
                </div>
            )}
        </Container>
    );
};

export default DevicePage;
