import React from 'react';
import Container from "react-bootstrap/Container";
import {Button, Card, Form, NavLink, Row} from "react-bootstrap";
import {LOGIN_ROUTE, REGISTRATION_ROUTE} from "../utils/consts";
import {useLocation} from "react-router-dom";

const Auth = () => {
    const location = useLocation()
    const isLogin= location.pathname === LOGIN_ROUTE
    return (
        <Container
            className="d-flex justify-content-center align-content-center"
            style={{height: window.innerHeight - 500}}
        >
            <Card style={{width: 600}} className="p-5">
                <h2 className="m-auto">{isLogin ? 'Login' : 'Register'}</h2>
                <Form className="d-flex flex-column">
                    <Form.Control
                        className="mt-2"
                        placeholder="enter email"
                    />
                    <Form.Control
                        className="mt-2"
                        placeholder="enter password"
                    />
                    <Row className="d-flex justify-content-between mt-3">
                        {isLogin ?
                            <div>
                                No account? <NavLink style={{color: "blue"}} to={REGISTRATION_ROUTE}>Register</NavLink>
                            </div>
                            :
                            <div>
                                Have account? <NavLink style={{color: "blue"}} to={LOGIN_ROUTE}>Login</NavLink>
                            </div>
                        }
                        {isLogin ?
                            <Button
                                className="mt-3"
                                variant={"outline-success"}>Login
                            </Button>
                            :
                            <Button
                                className="mt-3"
                                variant={"outline-success"}>Register
                            </Button>
                        }
                    </Row>
                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
