import React, { useState, useEffect } from 'react';

const AdminOrders = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchOrders();
    }, []);

    // Функция для получения заказов
    const fetchOrders = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/orders');

            // Если статус не OK (например 404 или 500)
            if (!response.ok) {
                throw new Error('Ошибка на сервере');
            }

            const data = await response.json();

            // Проверяем, что мы получили массив заказов
            if (Array.isArray(data)) {
                setOrders(data);
            } else {
                setError('Получены некорректные данные');
            }
        } catch (error) {
            // Обрабатываем ошибку запроса или JSON
            console.error('Ошибка при загрузке заказов:', error);
            setError(error.message || 'Что-то пошло не так');
        } finally {
            setLoading(false); // Завершаем загрузку
        }
    };

    // Если данные загружаются
    if (loading) {
        return <div>Загрузка...</div>;
    }

    // Если произошла ошибка
    if (error) {
        return <div>Ошибка: {error}</div>;
    }

    return (
        <div className="orders-container">
            {orders.map((order) => (
                <div key={order.id} className="order">
                    <h3>Заказ ID: {order.id}</h3>
                    <p>Общая стоимость: {order.totalPrice}₽</p>

                    {order.address ? (
                        <div className="address">
                            <p>Пользователь Email: <span>{order.user.email}</span></p>
                            <p>Адрес: <span>{order.address.address}</span></p>
                        </div>
                    ) : (
                        <p className="no-address">Адрес не найден</p>
                    )}

                    <div className="order-devices">
                        <h4>Устройства:</h4>
                        <ul>
                            {order.orderDevices?.map((orderDevice) => (
                                <li key={orderDevice.id}>
                                    {orderDevice.device.name}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            ))}
        </div>

    );
};

export default AdminOrders;
