import React from 'react';
import { useBasket } from '../context/BasketContext';

const BasketPage = () => {
    const { basket, removeFromBasket } = useBasket();  // Получаем корзину и функцию удаления товара

    return (
        <div>
            <h2>Корзина</h2>
            {basket.length === 0 ? (
                <p>Корзина пуста</p>
            ) : (
                <ul>
                    {basket.items.map(item => (
                        <div key={device.id}>
                            <img src={device.img} alt={device.name} width="100" /> {/* Отображаем картинку */}
                            <p>{device.name}</p>
                            <p>{device.price} ₽</p>
                        </div>
                    ))}

                </ul>
            )}
        </div>
    );
};

export default BasketPage;
