import React, { createContext, useState, useContext, useEffect } from 'react';

// Создаем контекст корзины
const BasketContext = createContext();

// Хук для использования корзины
export const useBasket = () => useContext(BasketContext);

// Провайдер для корзины
export const BasketProvider = ({ children }) => {
    const [basket, setBasket] = useState(() => {
        // При инициализации состояния корзины, пытаемся загрузить данные из localStorage
        const savedBasket = localStorage.getItem('basket');
        return savedBasket ? JSON.parse(savedBasket) : [];
    });

    // Сохраняем корзину в localStorage при изменении состояния
    useEffect(() => {
        localStorage.setItem('basket', JSON.stringify(basket));
    }, [basket]);

    // Функция для добавления товара в корзину
    const addToBasket = (device) => {
        setBasket((prevBasket) => [...prevBasket, device]);
    };

    // Функция для удаления товара из корзины
    const removeFromBasket = (deviceId) => {
        setBasket((prevBasket) => prevBasket.filter(item => item.id !== deviceId));
    };

    // Функция для очистки корзины
    const clearBasket = () => {
        setBasket([]);
    };

    return (
        <BasketContext.Provider value={{ basket, addToBasket, removeFromBasket, clearBasket }}>
            {children}
        </BasketContext.Provider>
    );
};
