import '../styles/PaymentPage.css';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBasket } from '../context/BasketContext';  // Импортируем контекст корзины
import { SHOP_ROUTE } from '../utils/consts';  // Путь к странице магазина
import axios from 'axios';  // Импортируем axios для отправки запросов
import '../styles/PaymentPage.css';
import * as cartItems from "react-bootstrap/ElementChildren";  // Подключаем стили

const PaymentPage = () => {
    const { basket, clearBasket } = useBasket();  // Получаем корзину и метод для очистки корзины
    const [paymentStatus, setPaymentStatus] = useState(null);  // Состояние для статуса платежа
    const [paymentMessage, setPaymentMessage] = useState("");  // Сообщение для отображения
    const [address, setAddress] = useState("");  // Состояние для хранения адреса
    const navigate = useNavigate();

    const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);  // Учитываем количество товаров

    const handlePayment = async () => {
        if (!address) {
            setPaymentMessage('Пожалуйста, введите ваш адрес.');
            setPaymentStatus('error');
            return;
        }

        const isPaymentSuccessful = true;  // Здесь будет ваша логика платежа

        if (isPaymentSuccessful) {
            setPaymentMessage('Заказ прошел успешно!');
            setPaymentStatus('success');
            clearBasket();

            try {
                // Если корзина пуста, покажем ошибку
                if (basket.length === 0) {
                    throw new Error('Корзина пуста');
                }

                const devices = basket.map(item => ({ deviceId: item.id }));

                console.log('Devices:', devices);
                console.log('Total Price:', totalPrice);

                const userId = localStorage.getItem("userId");

                const response = await fetch("http://localhost:5000/api/orders", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${localStorage.getItem("token")}`,
                    },
                    body: JSON.stringify({
                        userId, // ✅ Передаём userId
                        address: address,
                        totalPrice: totalPrice,
                        orderDevices: cartItems.map(item => ({
                            deviceId: item.id,
                            price: item.price,
                        })),
                    }),
                });



                setPaymentMessage('Ваш заказ был успешно создан!');
                setPaymentStatus('success');

            } catch (error) {
                console.error("Ошибка при создании заказа:", error);
                setPaymentMessage(error.message || 'Ошибка при создании заказа');
                setPaymentStatus('error');
            }

            // Перенаправляем в магазин через 2 секунды
            setTimeout(() => {
                navigate(SHOP_ROUTE);
            }, 200);

        } else {
            setPaymentMessage('Ошибка при оплате');
            setPaymentStatus('error');
        }
    };


    return (
        <div className="payment-page">
            <h2>Оформление заказа</h2>

            <div className="payment-items">
                <h3>Товары в заказе:</h3>
                <ul>
                    {basket.map((item) => (
                        <li key={item.id} className="payment-item">
                            {item.name} - {item.price} ₽
                        </li>
                    ))}
                </ul>
            </div>

            <div className="total-price">
                <h3>Итого: <strong>{totalPrice} ₽</strong></h3>
            </div>

            <div className="address-input">
                <label htmlFor="address">Введите ваш адрес:</label>
                <input
                    type="text"
                    id="address"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Введите ваш адрес"
                />
            </div>

            <div className="payment-instruction">
                <p><strong>Важно:</strong> После оформления заказа и перехода к оплате, выберите способ оплаты «Наличные при получении». Ваши товары будут доставлены по указанному адресу. При получении заказа передайте курьеру нужную сумму. Пожалуйста, убедитесь, что все товары в заказе верны и в хорошем состоянии. Благодарим за покупку!</p>
            </div>

            {paymentStatus === 'success' && (
                <p className="success-message">{paymentMessage}</p>
            )}

            {paymentStatus === 'error' && (
                <p className="error-message">{paymentMessage}</p>
            )}

            <button onClick={handlePayment} className="payment-btn">
                Заказать
            </button>
        </div>
    );
};

export default PaymentPage;
