import React, { useContext, useEffect, useState } from 'react';
import { BrowserRouter } from "react-router-dom";
import AppRouter from "./components/AppRouter";
import NavBar from './components/NavBar';
import { observer } from "mobx-react-lite";
import { Context } from "./index";
import { check } from "./http/userAPI";
import { Spinner } from "react-bootstrap";
import { BasketProvider } from './context/BasketContext'; // Импортируем BasketProvider

const App = observer(() => {
    const { user } = useContext(Context);
    const [loading, setLoading] = useState(true);

    // При загрузке приложения проверяем наличие токена
    useEffect(() => {
        const token = localStorage.getItem('token');  // Проверяем токен в localStorage

        if (token) {
            check()
                .then(data => {
                    if (data) {
                        user.setUser(data);
                        user.setIsAuth(true);
                    }
                })
                .catch(() => {
                    user.setUser({});
                    user.setIsAuth(false);
                    localStorage.removeItem("token");
                })
                .finally(() => setLoading(false)); // Завершаем загрузку после проверки
        } else {
            user.setUser({});
            user.setIsAuth(false);
            setLoading(false); // Завершаем загрузку, если нет токена
        }
    }, [user]); // Добавляем user в зависимости

    // Если состояние еще не загружено, показываем Spinner
    if (loading) {
        return <Spinner animation={'grow'} />;
    }

    return (
        // Оборачиваем приложение в BasketProvider, чтобы все компоненты имели доступ к корзине
        <BasketProvider>
            <BrowserRouter>
                <NavBar />
                {/* Пропс loading больше не передаем, так как он не используется в AppRouter */}
                <AppRouter />
            </BrowserRouter>
        </BasketProvider>
    );
});

export default App;
