import React, { createContext, useContext, useState } from 'react';

// Создаем контекст для корзины
const BasketContext = createContext();

// Хук для использования контекста
export const useBasket = () => {
    return useContext(BasketContext);
};

// Компонент провайдера для корзины
export const BasketProvider = ({ children }) => {
    // Состояние для корзины
    const [basket, setBasket] = useState([]);

    // Функция для добавления товара в корзину
    const addToBasket = (device) => {
        setBasket((prevBasket) => [...prevBasket, device]);
    };

    // Функция для удаления товара из корзины
    const removeFromBasket = (deviceId) => {
        setBasket((prevBasket) => prevBasket.filter(item => item.id !== deviceId));
    };

    // Функция для очистки корзины
    const clearBasket = () => {
        setBasket([]);
    };

    // Возвращаем контекст с доступными функциями
    return (
        <BasketContext.Provider value={{ basket, addToBasket, removeFromBasket, clearBasket }}>
            {children}
        </BasketContext.Provider>
    );
};
