import React, { useContext } from 'react';
import { Button, Nav, Navbar, Container } from 'react-bootstrap';
import { NavLink, useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, LOGIN_ROUTE, SHOP_ROUTE } from "../utils/consts";
import { Context } from "../index";

const NavBar = () => {
    const { user } = useContext(Context); // получаем данные о пользователе
    const history = useNavigate();

    const logOut = () => {
        localStorage.removeItem('token');  // Удаляем токен из localStorage
        user.setUser({});  // Сбрасываем данные пользователя в состоянии
        user.setIsAuth(false);  // Сбрасываем статус авторизации
    };

    return (
        <Navbar bg="dark" variant="dark">
            <Container>
                <NavLink style={{ color: 'white' }} to={SHOP_ROUTE}>КупиДевайс</NavLink>
                {user.isAuth ? (
                    <Nav className="ml-auto" style={{ color: 'white' }}>
                        <Button variant={"outline-light"} onClick={() => history(ADMIN_ROUTE)}>
                            Админ панель
                        </Button>
                        <Button
                            variant={"outline-light"}
                            onClick={() => logOut()}
                            className="ml-2"
                        >
                            Выйти
                        </Button>
                    </Nav>
                ) : (
                    <Nav className="ml-auto" style={{ color: 'white' }}>
                        <Button variant={"outline-light"} onClick={() => history(LOGIN_ROUTE)}>
                            Авторизация
                        </Button>
                    </Nav>
                )}
            </Container>
        </Navbar>
    );
};

export default NavBar;
