import React, { useState, useEffect } from 'react';

const AdminOrders = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchOrders();
    }, []);

    // Функция для получения заказов
    const fetchOrders = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/orders');

            // Если статус не OK (например 404 или 500)
            if (!response.ok) {
                throw new Error('Ошибка на сервере');
            }

            const data = await response.json();

            // Проверяем, что мы получили массив заказов
            if (Array.isArray(data)) {
                setOrders(data);
            } else {
                setError('Получены некорректные данные');
            }
        } catch (error) {
            // Обрабатываем ошибку запроса или JSON
            console.error('Ошибка при загрузке заказов:', error);
            setError(error.message || 'Что-то пошло не так');
        } finally {
            setLoading(false); // Завершаем загрузку
        }
    };

    return (
        <div>
            {loading && <p>Загрузка заказов...</p>}
            {error && <p style={{ color: 'red' }}>Ошибка: {error}</p>}
            {orders && orders.length > 0 ? (
                <div>
                    {orders.map((order) => (
                        <div key={order.id}>
                            <h3>Заказ ID: {order.id}</h3>
                            <p>Общая стоимость: {order.totalPrice ? order.totalPrice : 'Не указана'}₽</p>

                            {/* Добавляем информацию о пользователе */}
                            {order.user && (
                                <div>
                                    <p>Пользователь ID: {order.user.id}</p>
                                    <p>Пользователь Email: {order.user.email}</p>

                                    {/* Отображаем адрес пользователя */}
                                    <p>Адрес: {order.user.address ? order.user.address : 'Не найден'}</p>
                                </div>
                            )}

                            {/* Пример отображения устройств в заказе */}
                            <h4>Устройства:</h4>
                            {order.orderDevices && order.orderDevices.length > 0 ? (
                                <ul>
                                    {order.orderDevices.map((orderDevice) => (
                                        <li key={orderDevice.id}>
                                            {orderDevice.device?.name ? orderDevice.device.name : 'Неизвестное устройство'} - {orderDevice.price ? orderDevice.price : 'Не указана'}₽
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>Нет устройств в заказе</p>
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <p>Нет заказов</p>
            )}
        </div>
    );
};

export default AdminOrders;
