import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const $host = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
});

const $authHost = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
});

const authInterceptor = config => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.authorization = `Bearer ${token}`;
    }
    return config;
};

const errorInterceptor = (error) => {
    if (error.response && error.response.status === 401) {
        // Перенаправление на страницу логина при ошибке 401
        window.location.href = '/login'; // можно использовать history.push() или navigate, если работаешь с React Router
    }
    return Promise.reject(error);
};

$authHost.interceptors.request.use(authInterceptor);
$authHost.interceptors.response.use(response => response, errorInterceptor);

export { $host, $authHost };
